    return (
        <div key={book.id} className="item-Book">
            <div 
                className={ "item-book item-text"}

                >
            <div className="item-delete" onClick={() => removeBook(book.id)}>
                Удалить
            </div>
               <h1>{book.Name}</h1>
               <center> <h3>{book.Author}</h3><img width='145' height='205' src={`data:image/png;base64,${book.Image}`}/></center> 


            </div>

        </div>
    )
}

export default Book
