import Book from './components/book/Book'
import BookForm from './components/book/BookForm'
import { useLocalStorage } from "./hooks/useLocalStorage";
function App() {
  const [books, setBooks] = useLocalStorage("books", []);
  const addBook = (userInput, userInput1,imageBase64) => {
    if(userInput) 
    if(userInput1) {
      const newItem = {
        id: Math.random().toString(36).substr(2,9),
        Name: userInput,
        Author: userInput1,     
        Image: imageBase64?imageBase64:"data:image/png;base64,"   
      }
      setBooks([...books, newItem])

    }
  }
  const removeBook = (id) => {
    setBooks([...books.filter((book) => book.id !== id)])
  }
  return (
    <div className="App">    
      <div key={'AddNewBookForm'} className={"item-book"}>
        <div className={ "item-new-form"} >
        <div className="item-delete" >
                Всего книг: {books.length}
            </div>
          <center><h1>Мои книги react hooks. </h1></center>
        </div>
      </div>
      <div key={'AddNewBookForm'} className={"item-book"}>
        <div className={ "item-new-form"} >
          <BookForm addBook={addBook} />
        </div>
      </div>
      {books.map((book) => {
        return (
          <Book
            book={book}
            key={book.id}
            removeBook={removeBook}
            />
        )
      })}
    </div>
  );
}
export default App;
