import { useState } from 'react'

function BookForm({ addBook }) {
    const [userInput1, setUserInput1] = useState('')
    const [userInput,setUserInput] = useState('')
    const [file, setFile] = useState('');
    const fileHandler = (e) => {
        console.log('hjghfv  ', e.target.files[0]);
        let { _file } = file;
        _file = e.target.files[0];

        getBase64(_file)
          .then(result => {
            result=_file["base64"];
            console.log("File Is", result);
            setFile(result)
          })
          .catch(err => {
            console.log('Errr!!! ',err);
          });

      }
  const handleChange1 = (e) => {
        setUserInput1(e.currentTarget.value)
    }
   const getBase64 = (img) => {
        return new Promise(resolve => {
          let fileInfo;
          let baseURL = "";
          // Make new FileReader
          let reader = new FileReader();

          // Convert the file to base64 text
          reader.readAsDataURL(img);

          // on reader load somthing...
          reader.onload = () => {
            // Make a fileInfo Object
            console.log("Called", reader);
            baseURL = reader.result;
            console.log('url ',baseURL);
            resolve(baseURL);
          };
          console.log('url info ',fileInfo);
        });
      };




    const handleKeyPress1 = (e) => {
        if(e.key === "Enter") {
        }
    }

    const handleChange = (e) => {
        setUserInput(e.currentTarget.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        addBook(userInput,userInput1,file)
        setUserInput("")
        setFile("")
        setUserInput1("")
    }

    const handleKeyPress = (e) => {
        if(e.key === "Enter") {
            handleSubmit(e)
        }
    }

    return (
        <form onSubmit={handleSubmit}>
        <table width='100%' height='100%'>
        <tr>
        <td width='46%'>  
             <input 
                value={userInput1}
                type="text"
                onChange={handleChange1}
                onKeyDown={handleKeyPress1}
                placeholder="Введите автора..."
            />      
         </td>
         <td width='8%'>  
        </td> 
        <td  width='46%'>
        <input 
                value={userInput}
                type="text"
                onChange={handleChange}
                onKeyDown={handleKeyPress}
                placeholder="Введите название..."
            />
        </td>
    </tr>
    <tr>
        <td colspan="3"><center>{file ? <img width='145' height='205' alt='предпросмотр обложки' src={`data:image/png;base64,${file}`}/>:''}</center></td>
    </tr>
    <tr>
        <td><input type="file" onChange={fileHandler} value={null}/></td>
        <td></td>
        <td ><button>Сохранить</button></td>
    </tr>            </table>
        </form>
    )
}

export default BookForm
