function Book({ book,  removeBook }) {
    return (
        <div key={book.id} className="item-Book">
            <div 
                className={ "item-book item-text"}

                >
                Книга №: {book.id}<br></br>
                Название: {book.Name}<br></br> 
                Автор: {book.Author}   <br></br>
                Обложка: {book.Author}
            </div>
            <div className="item-delete" onClick={() => removeBook(book.id)}>
                X
            </div>
        </div>
    )
}

export default Book
