        if(e.key === "Enter") {
        }
    }

    const handleChange = (e) => {
        setUserInput(e.currentTarget.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        addBook(userInput,userInput1,file)
        setUserInput("")
        setUserInput1("")
    }

    const handleKeyPress = (e) => {
        if(e.key === "Enter") {
            handleSubmit(e)
        }
    }

    return (
        <form onSubmit={handleSubmit}>
        <table width='100%' height='100%'>
        <tr>
        <td width='46%'>  
             <input 
                value={userInput1}
                type="text"
                onChange={handleChange1}
                onKeyDown={handleKeyPress1}
                placeholder="Введите автора..."
            />      
         </td>
         <td width='8%'>  
        </td> 
        <td  width='46%'>
        <input 
                value={userInput}
                type="text"
                onChange={handleChange}
                onKeyDown={handleKeyPress}
                placeholder="Введите название..."
            />
        </td>
    </tr>
    <tr>
        <td colspan="3"><center>{file ? <img width='145' height='205' src={`data:image/png;base64,${file}`} onClick={fileHandler}/>:''}</center></td>
    </tr>
    <tr>
        <td><input type="file" onChange={fileHandler} value={null}/></td>
        <td></td>
        <td ><button>Сохранить</button></td>
    </tr>            </table>
        </form>
    )
}

export default BookForm
