import { useState } from 'react'

function BookForm({ addBook }) {
    const [userInput1, setUserInput1] = useState('')
    const [userInput,setUserInput] = useState('')
    const [file, setFile] = useState('');
    const fileHandler = event => {
        console.log(event.target.files[0]);
        let reader = new FileReader();
        reader.onload = function(e) {
          setFile(e.target.result);
        };
        reader.readAsDataURL(event.target.files[0]);
      };
  const handleChange1 = (e) => {
        setUserInput1(e.currentTarget.value)
    }


    const handleKeyPress1 = (e) => {
        if(e.key === "Enter") {
        }
    }

    const handleChange = (e) => {
        setUserInput(e.currentTarget.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        addBook(userInput,userInput1,file)
        setUserInput("")
        setUserInput1("")
    }

    const handleKeyPress = (e) => {
        if(e.key === "Enter") {
            handleSubmit(e)
        }
    }

    return (
        <div key={'AddNewBookForm'} className="item-book item-new-form">
            <form onSubmit={handleSubmit}>
            <input 
                value={userInput1}
                type="text"
                onChange={handleChange1}
                onKeyDown={handleKeyPress1}
                placeholder="Введите автора..."
            />      
             <input 
                value={userInput}
                type="text"
                onChange={handleChange}
                onKeyDown={handleKeyPress}
                placeholder="Введите название..."
            />
            <br/>    
            {file ? 
      <img width='145' height='205' src={`data:image/png;base64,${file}`} onClick={fileHandler}/>:  
   ''}
      <input type="file" onChange={fileHandler} value={null}/>
            <button>Сохранить</button>
        <br/>
        </form>
        </div>
    )
}

export default BookForm
